// src/components/SustainableBrands.js
import React, { useState } from 'react';
import './SustainableBrands.css';

function SustainableBrands() {
  const [brandName, setBrandName] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!brandName.trim()) {
      setError('Please enter a brand name');
      return;
    }
    setLoading(true);
    setError('');
    try {
      const response = await fetch('/api/sustainable-brands', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ brandName: brandName.trim() })
      });
      const data = await response.json();
      if (data.error) {
        setError(data.error);
      } else {
        setResult(data);
      }
    } catch (err) {
      console.error('Error analyzing brand:', err);
      setError('Failed to analyze brand. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sustainable-brands">
      <h1>Is Your Favorite Brand Sustainable-friendly?</h1>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={brandName} 
          onChange={(e) => setBrandName(e.target.value)} 
          placeholder="Enter brand name..." 
          required 
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Analyzing...' : 'Analyze Brand'}
        </button>
      </form>
      {result && (
        <div>
          <h2>Sustainability Score: {result.score}%</h2>
          <h3>Context</h3>
          <p>{result.context}</p>
        </div>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
}

export default SustainableBrands;
