import React, { useState } from 'react';
import axios from 'axios';
import './SustainableBrands.css';

function SustainableBrands() {
  const [brandName, setBrandName] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Handler for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!brandName.trim()) {
      setError('Please enter a brand name');
      return;
    }
    setLoading(true);
    setError('');
    setAnalysis(null);

    try {
      // POST request to your Flask backend route
      const response = await axios.post('/api/sustainable-brands', {
        brandName: brandName.trim()
      });
      setAnalysis(response.data);
    } catch (err) {
      console.error('Error analyzing brand:', err);
      setError('Failed to analyze brand. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Render gauge meter from the "score" returned by the API
  const renderGaugeMeter = () => {
    if (!analysis) return null;
    const score = analysis.score || 0;
    const angle = (score / 100) * 180;
    // If score > 50 => green fill, else red fill
    const fillColor = score > 50 ? '#4CAF50' : '#FF5252';

    return (
      <div className="gauge-container">
        <div className="gauge">
          <div
            className="gauge-fill"
            style={{
              transform: `rotate(${angle}deg)`,
              backgroundColor: fillColor
            }}
          />
          <div className="gauge-cover">
            <span>{score}%</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="sustainable-brands">
      {/* Background Video */}
      <video autoPlay loop muted playsInline className="brand-bg-video">
        <source
          src="https://cdn.pixabay.com/video/2017/01/12/7241-199190972_large.mp4"
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>

      <div className="sustainable-brands-content">
        <h1>Is Your Favorite Brand Sustainable-friendly?</h1>

        {/* Form for brand input */}
        <form className="brand-form" onSubmit={handleSubmit}>
          <input
            className="brand-input"
            type="text"
            placeholder="Enter Brand Name"
            value={brandName}
            onChange={(e) => setBrandName(e.target.value)}
          />
          <button
            className="brand-button"
            type="submit"
            disabled={loading}
          >
            {loading ? 'Analyzing...' : 'Analyze'}
          </button>
        </form>

        {error && <p className="error">{error}</p>}

        {/* If we have analysis from backend, show results */}
        {analysis && !error && (
          <div className="analysis-results">
            <h2>{analysis.brandName} Sustainability Analysis</h2>
            {renderGaugeMeter()}

            <div className="analysis-text">
              <h3>Detailed Analysis</h3>
              <div className="report-content">
                {/* The backend splits into "analysis" and "context" if found. */}
                {analysis.analysis}
              </div>

              {/* If there's a context summary in the response, show it */}
              {analysis.context && (
                <>
                  <h3>Context Summary</h3>
                  <div className="report-content">
                    {analysis.context}
                  </div>
                </>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default SustainableBrands;
