// src/components/SustainableBrands.js
import React, { useState } from 'react';
import './SustainableBrands.css';

// Embed your Google API key (NOTE: this exposes your key on the client!)
const GOOGLE_API_KEY = "AIzaSyBY_24wnWmxwNpjAzdi2CUQGJLfZh7b61U";
// Define your model ID – adjust if needed. (Replace "gemini-pro" with the correct model if different.)
const MODEL_ID = "gemini-pro";
// Base URL for Google Generative Language API (v1beta2); adjust endpoint as needed.
const API_BASE_URL = `https://generativelanguage.googleapis.com/v1beta2/models/${MODEL_ID}:generate?key=${GOOGLE_API_KEY}`;

function SustainableBrands() {
  const [brandName, setBrandName] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Function to call the generative API with a prompt
  const generateContent = async (prompt) => {
    try {
      const response = await fetch(API_BASE_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          prompt: {
            text: prompt
          },
          temperature: 0.7,
          candidate_count: 1
        })
      });
      const data = await response.json();
      // Assuming the API returns an object with a 'candidates' array and each candidate has a 'output' field.
      if (data && data.candidates && data.candidates.length > 0) {
        return data.candidates[0].output;
      }
      return "";
    } catch (err) {
      console.error("Error in generateContent:", err);
      throw err;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!brandName.trim()) {
      setError('Please enter a brand name');
      return;
    }
    setLoading(true);
    setError('');
    try {
      // Build prompt for analysis with context
      const analysisPrompt = `Gather and process information from various sources—including corporate sustainability reports, news articles, and online reviews—about the brand "${brandName}". Provide a detailed analysis of its sustainability efforts (energy efficiency, waste reduction, ethical sourcing, etc.) and include a brief contextual summary. Separate the detailed analysis and the context summary with the delimiter "CONTEXT:"`;
      const analysisText = await generateContent(analysisPrompt);
      let detailedAnalysis = analysisText;
      let contextSummary = "No additional context provided.";
      if (analysisText.includes("CONTEXT:")) {
        const parts = analysisText.split("CONTEXT:");
        detailedAnalysis = parts[0].trim();
        contextSummary = parts[1].trim();
      }
      
      // Build prompt for score
      const scorePrompt = `Provide only a JSON object containing the sustainable score of the brand in the following format: {"score": "x%"} (do not include any additional text)`;
      const scoreResponseText = await generateContent(scorePrompt);
      const match = scoreResponseText.match(/(\d+)%/);
      let scoreValue = match ? parseFloat(match[1]) : 50;
      
      setAnalysis({
        brandName: brandName.trim(),
        analysis: detailedAnalysis,
        context: contextSummary,
        score: scoreValue
      });
    } catch (err) {
      console.error('Error analyzing brand:', err);
      setError('Failed to analyze brand. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const renderGaugeMeter = () => {
    if (!analysis) return null;
    const { score } = analysis;
    const angle = (score / 100) * 180;
    return (
      <div className="gauge-container">
        <div className="gauge">
          <div
            className="gauge-fill"
            style={{
              transform: `rotate(${angle}deg)`,
              backgroundColor: score > 50 ? '#4CAF50' : '#FF5252'
            }}
          ></div>
          <div className="gauge-cover">
            <span>{score}%</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="sustainable-brands">
      {/* Background Video */}
      <video autoPlay loop muted playsInline className="bg-video">
        <source
          src="https://cdn.pixabay.com/video/2017/01/12/7241-199190972_large.mp4"
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>
      {/* (Optional overlay if needed) */}
      <div className="video-overlay"></div>
      
      <h1>Is Your Favorite Brand Sustainable-friendly?</h1>
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            value={brandName}
            onChange={(e) => setBrandName(e.target.value)}
            placeholder="Enter brand name..."
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Analyzing...' : 'Analyze Brand'}
          </button>
        </div>
        {error && <p className="error">{error}</p>}
      </form>
      
      {analysis && (
        <div className="analysis-results">
          <h2>Sustainability Analysis for {analysis.brandName}</h2>
          {renderGaugeMeter()}
          <div className="analysis-text">
            <h3>Sustainability Report</h3>
            <div className="report-content">
              {analysis.analysis.split('\n').map((paragraph, idx) => (
                <p key={idx}>{paragraph}</p>
              ))}
            </div>
            <div className="analysis-context">
              <h3>Context</h3>
              <div className="report-content">
                <p>{analysis.context}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SustainableBrands;
