import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './EnergyAnalyzer.css';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function EnergyAnalyzer() {
  const [monthlyConsumption, setMonthlyConsumption] = useState(500);
  const [householdSize, setHouseholdSize] = useState(2);
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/energy-analyzer', {
        monthlyConsumption: parseFloat(monthlyConsumption),
        householdSize: parseInt(householdSize),
      });
      setResults(response.data);
    } catch (error) {
      console.error('Error analyzing energy data:', error);
    } finally {
      setLoading(false);
    }
  };

  const renderBellCurve = () => {
    if (!results) return null;

    const { bellCurveData } = results;

    const data = {
      labels: bellCurveData.x,
      datasets: [
        {
          label: 'Energy Usage Distribution',
          data: bellCurveData.y.map((yVal, i) => ({
            x: bellCurveData.x[i],
            y: yVal,
          })),
          borderColor: '#fff', // white line
          backgroundColor: 'rgba(255, 255, 255, 0.1)', // faint fill
          fill: true,
          tension: 0.4,
        },
        {
          label: 'Your Usage',
          data: [
            {
              x: bellCurveData.usage,
              y: bellCurveData.usage_y,
            },
          ],
          backgroundColor: '#fff', // white point
          borderColor: '#fff',
          pointRadius: 8,
          pointHoverRadius: 10,
          showLine: false,
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top',
          labels: {
            color: '#fff', // white text
          },
        },
        title: {
          display: true,
          text: 'Per Capita Monthly Energy Usage Distribution',
          color: '#fff', // white title
        },
      },
      scales: {
        x: {
          title: {
            display: true,
            text: 'Usage (kWh/Month Per Capita)',
            color: '#fff',
          },
          ticks: {
            color: '#fff',
          },
          grid: {
            color: 'rgba(255,255,255,0.2)',
          },
        },
        y: {
          title: {
            display: true,
            text: 'Density',
            color: '#fff',
          },
          ticks: {
            color: '#fff',
          },
          grid: {
            color: 'rgba(255,255,255,0.2)',
          },
        },
      },
    };

    return <Line data={data} options={options} />;
  };

  const renderSustainabilityScore = () => {
    if (!results) return null;

    const { sustainabilityScore } = results;
    // Show only integer percentage
    const roundedScore = Math.round(sustainabilityScore);

    return (
      <div className="sustainability-score">
        <h2>Your Sustainability Score üçÉ</h2>
        <div className="gauge-container">
          <div className="gauge">
            <div
              className="gauge-fill"
              style={{
                transform: `rotate(${(roundedScore / 100) * 180}deg)`,
              }}
            />
            <div className="gauge-cover">
              <span>{roundedScore}%</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="energy-analyzer">
      {/* Background Video */}
      <video autoPlay loop muted playsInline className="energy-bg-video">
        <source
          src="https://cdn.pixabay.com/video/2022/06/21/121429-724697304_tiny.mp4"
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>

      <div className="energy-analyzer-content">
        <h1>‚ö° Sustainable Energy Consumption Analyzer</h1>

        <form onSubmit={handleSubmit} className="energy-form">
          <div className="form-group">
            <label>Total Monthly Household Energy Consumption (kWh)</label>
            <input
              type="number"
              min="0"
              value={monthlyConsumption}
              onChange={(e) => setMonthlyConsumption(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Number of People in Your Household</label>
            <input
              type="number"
              min="1"
              value={householdSize}
              onChange={(e) => setHouseholdSize(e.target.value)}
              required
            />
          </div>

          <button type="submit" disabled={loading}>
            {loading ? 'Analyzing...' : 'Analyze'}
          </button>
        </form>

        {results && (
          <div className="results">
            <div className="chart-container">{renderBellCurve()}</div>
            {renderSustainabilityScore()}

            <div className="recommendations">
              <h2>Personalized Recommendations</h2>
              <ul>
                {results.recommendations.map((recommendation, index) => (
                  <li key={index}>{recommendation}</li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default EnergyAnalyzer;
