// src/components/SustainableBrands.js
import React, { useState } from 'react';
import './SustainableBrands.css';

// --- EMBEDDED API KEY (for testing only) ---
const OPENAI_API_KEY = "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";

const OPENAI_API_URL = "https://api.openai.com/v1/chat/completions";

async function generateContent(prompt) {
  try {
    const response = await fetch(OPENAI_API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${OPENAI_API_KEY}`
      },
      body: JSON.stringify({
        model: "gpt-4",
        messages: [{ role: "user", content: prompt }],
        temperature: 0.7
      })
    });
    const data = await response.json();
    if (data.choices && data.choices.length > 0) {
      return data.choices[0].message.content;
    }
    return "";
  } catch (err) {
    console.error("Error in generateContent:", err);
    throw err;
  }
}

function SustainableBrands() {
  const [brandName, setBrandName] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!brandName.trim()) {
      setError('Please enter a brand name');
      return;
    }
    setLoading(true);
    setError('');
    try {
      const analysisPrompt = `Provide a detailed sustainability analysis for the brand "${brandName}" and include a comprehensive context summary.`;
      const analysisText = await generateContent(analysisPrompt);
      let detailedAnalysis = analysisText;
      let contextSummary = "No additional context provided.";
      if (analysisText.includes("CONTEXT:")) {
        const parts = analysisText.split("CONTEXT:");
        detailedAnalysis = parts[0].trim();
        contextSummary = parts[1].trim() || "Context not available.";
      }
      setAnalysis({
        brandName: brandName.trim(),
        analysis: detailedAnalysis,
        context: contextSummary
      });
    } catch (err) {
      console.error('Error analyzing brand:', err);
      setError('Failed to analyze brand. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sustainable-brands">
      <h1>Is Your Favorite Brand Sustainable-friendly?</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={brandName} onChange={(e) => setBrandName(e.target.value)} placeholder="Enter brand name..." required />
        <button type="submit" disabled={loading}>{loading ? 'Analyzing...' : 'Analyze Brand'}</button>
      </form>
      {analysis && (
        <div>
          <h2>Sustainability Score: {analysis.score}%</h2>
          <h3>Context</h3>
          <p>{analysis.context}</p>
        </div>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
}

export default SustainableBrands;
