// src/components/SustainableBrands.js
import React, { useState } from 'react';
import './SustainableBrands.css';

const OPENAI_API_KEY = "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";
const OPENAI_API_URL = "https://api.openai.com/v1/chat/completions";

async function generateContent(prompt) {
  try {
    const response = await fetch(OPENAI_API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${OPENAI_API_KEY}`
      },
      body: JSON.stringify({
        model: "gpt-4",
        messages: [{ role: "user", content: prompt }],
        temperature: 0.7
      })
    });
    const data = await response.json();
    if (data.choices && data.choices.length > 0) {
      return data.choices[0].message.content;
    }
    return "";
  } catch (err) {
    console.error("Error in generateContent:", err);
    throw err;
  }
}

function SustainableBrands() {
  const [brandName, setBrandName] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [news, setNews] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Function to fetch sustainability news for the brand
  const fetchNews = async (brand) => {
    try {
      const response = await fetch('/api/sustainable-news', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json'
         },
         body: JSON.stringify({ brand })
      });
      const data = await response.json();
      return data.articles;
    } catch (err) {
      console.error('Error fetching news:', err);
      return [];
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!brandName.trim()) {
      setError('Please enter a brand name');
      return;
    }
    setLoading(true);
    setError('');
    try {
      // Fetch sustainability analysis
      const analysisPrompt = `Gather and process information about the brand "${brandName}". Provide a detailed analysis of its sustainability efforts and include a brief context summary. Separate the detailed analysis and context summary with the delimiter "CONTEXT:"`;
      const analysisText = await generateContent(analysisPrompt);
      let detailedAnalysis = analysisText;
      let contextSummary = "No additional context provided.";
      if (analysisText.includes("CONTEXT:")) {
        const parts = analysisText.split("CONTEXT:");
        detailedAnalysis = parts[0].trim();
        contextSummary = parts[1].trim();
      }

      // Fetch sustainability score
      const scorePrompt = `Provide only a JSON object containing the sustainable score of the brand in the following format: {"score": "x%"} (do not include any additional text)`;
      const scoreResponseText = await generateContent(scorePrompt);
      const match = scoreResponseText.match(/(\\d+)%/);
      let scoreValue = match ? parseFloat(match[1]) : 50;

      setAnalysis({
        brandName: brandName.trim(),
        analysis: detailedAnalysis,
        context: contextSummary,
        score: scoreValue
      });

      // Fetch related sustainability news for the brand
      const articles = await fetchNews(brandName);
      setNews(articles);

    } catch (err) {
      console.error('Error analyzing brand:', err);
      setError('Failed to analyze brand. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="sustainable-brands">
      <video autoPlay loop muted playsInline className="bg-video">
        <source
          src="https://cdn.pixabay.com/video/2017/01/12/7241-199190972_large.mp4"
          type="video/mp4"
        />
        Your browser does not support the video tag.
      </video>
      <div className="video-overlay"></div>

      <h1>Is Your Favorite Brand Sustainable-friendly?</h1>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input
            type="text"
            value={brandName}
            onChange={(e) => setBrandName(e.target.value)}
            placeholder="Enter brand name..."
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Analyzing...' : 'Analyze Brand'}
          </button>
        </div>
        {error && <p className="error">{error}</p>}
      </form>

      {analysis && (
        <div className="analysis-results">
          <h2>Sustainability Analysis for {analysis.brandName}</h2>
          <h3>Sustainable Score: {analysis.score}%</h3>
          <div className="analysis-context">
            <h3>Context</h3>
            <div className="report-content">
              <p>{analysis.context}</p>
            </div>
          </div>
          <h3>Sustainability Report</h3>
          <div className="report-content">
            {analysis.analysis.split('\n').map((paragraph, idx) => (
              <p key={idx}>{paragraph}</p>
            ))}
          </div>
        </div>
      )}

      {news && news.length > 0 && (
        <div className="news-section">
          <h3>Latest Sustainability News for {brandName}</h3>
          <ul>
            {news.map((article, index) => (
              <li key={index}>
                <a href={article.url} target="_blank" rel="noopener noreferrer">{article.title}</a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SustainableBrands;
